mapper.select:
select p.seller_code from cart c join customer t on c.id = t.id join options o on o.product_code = c.product_code join product_list p on p.product_code= o.product_code join seller s on s.seller_code = p.seller_code where c.id= #{id} and c.cart_no= #{cart_no}
and c.option1=o.option1
order by s.seller_name;

mapper.update:
update options
set inventory= inventory-#{quantity}, accumulation= accumulation+#{quantity}
where product_code = #{product_code}
and option1=#{option1}
and option2=#{option2}
